{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyNUTmnSad9bohhckV+JnN3D"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"6BmAW-RIrsTG"},"outputs":[],"source":["import streamlit as st\n","from ultralytics import YOLO\n","from PIL import Image\n","import cv2\n","import numpy as np\n","\n","st.title(\"üçéüçåüçä Fruit Object Detection\")\n","\n","# Load model\n","model = YOLO(\"best.pt\")  # ensure best.pt is in same folder\n","\n","# Confidence slider\n","conf_threshold = st.slider(\"Confidence Threshold\", 0.1, 1.0, 0.5, 0.05)\n","\n","# Upload image\n","uploaded_file = st.file_uploader(\"Upload an image\", type=[\"jpg\", \"jpeg\", \"png\"])\n","\n","if uploaded_file is not None:\n","    img = Image.open(uploaded_file).convert(\"RGB\")\n","\n","    # Run inference\n","    results = model(img, conf=conf_threshold)\n","\n","    # Plot detections\n","    annotated = results[0].plot()\n","    annotated = cv2.cvtColor(annotated, cv2.COLOR_BGR2RGB)\n","\n","    st.image(annotated, caption=\"Detected Fruits\", use_column_width=True)\n","\n","    # Collect detected classes\n","    detected_info = []\n","    for box, cls, conf in zip(\n","        results[0].boxes.xyxy,\n","        results[0].boxes.cls,\n","        results[0].boxes.conf\n","    ):\n","        detected_info.append(f\"{model.names[int(cls)]}: {conf:.2f}\")\n","\n","    # Display results\n","    if detected_info:\n","        st.subheader(\"Detected Fruits:\")\n","        for item in detected_info:\n","            st.write(item)\n","    else:\n","        st.write(\"No fruits detected. Try lowering the confidence threshold.\")"]}]}